CREATE TABLE "harcelement" ("id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE , "name" VARCHAR(255) NOT NULL, "hash" VARCHAR(255) NOT NULL  UNIQUE , "email" VARCHAR(255) NOT NULL , "email_victim" VARCHAR(255) NOT NULL , "time" VARCHAR(255) NOT NULL , "subject" VARCHAR(255) NOT NULL , "message" TEXT NOT NULL  check(typeof("message") = 'text') , "next" DATETIME, "active" BOOL NOT NULL , "number_send" INTEGER NOT NULL  DEFAULT 0)
